import React, { useEffect } from "react";
import "./Navbar.css";

const Navbar = () => {
  useEffect(() => {
      window.addEventListener("scroll",()=>{
          if (window.scrollY > 100) {
              handleShow(true);
          }
      })
  }, []);
  return (
    <div className="nav">
      <img
        className="nav_logo"
        src="https://onlystyleremains.be/wp-content/uploads/2020/09/Netflix.png"
        alt="Netflix Logo"
      />
      <img
        className="nav_avatar"
        src="https://onlystyleremains.be/wp-content/uploads/2020/09/Netflix.png"
        alt="Netflix Logo"
      />
    </div>
  );
};

export default Navbar;
